var alt = require('../alt.jsx'),
    xhr = require('xhr'),
    Settings = require('../../settings.jsx'),
    TableListActions = require('../actions/TableListActions.jsx');

class TableListStore {
  constructor() {
    this.routes = {};
    this.meta = {};
    this.lastErr = null;
    this.isLoading = false;

    this.bindListeners({
      handleFetchRoutes: TableListActions.FETCH_ROUTES,
      handleFetchMeta: TableListActions.FETCH_META,
      handleUpdateRoutes: TableListActions.UPDATE_ROUTES,
      handleUpdateMeta: TableListActions.UPDATE_META,
      handleFetchRoutesFailed: TableListActions.FETCH_ROUTES_FAILED,
      handleFetchMetaFailed: TableListActions.FETCH_META_FAILED,
    });

    this.exportPublicMethods({
      getRoutes: this.getRoutes,
      isRoutesLoading: this.isRoutesLoading,
      getMeta: this.getMeta,
    });
  }

  handleFetchRoutes(data) {
    this.routes = {}
    this.isLoading = true;
    xhr(
      Settings.apiRoot,
      (err, resp, body) => {
        if (resp.statusCode >= 400) {
          TableListActions.fetchRoutesFailed(body);
        }

        var result = {};
        var json = JSON.parse(body);
        for (var resource in json) {
          var uri = json[resource];
          var endpoint = uri.slice(Settings.apiRoot.length).replace(/\/$/, '');
          result[resource] = endpoint;
          TableListActions.fetchMeta(endpoint);
        }
        TableListActions.updateRoutes(result);
      }
    );
  }

  handleFetchMeta(endpoint) {
    var options = {
      uri: Settings.apiRoot + endpoint + '/',
      method: 'OPTIONS',
    };
    xhr(
      options,
      (err, resp, body) => {
        if (resp.statusCode >= 400)
          TableListActions.fetchMetaFailed(endpoint, err);

        var data = JSON.parse(body);
        TableListActions.updateMeta({
          endpoint: endpoint,
          meta: {
            name: data.name,
            description: data.description,
            ownedFields: data.owned_fields
          }
        });
      }
    );
  }

  handleUpdateRoutes(routes) {
    this.isLoading = false;
    this.routes = routes;
  }

  handleUpdateMeta(data) {
    var endpoint = data.endpoint;
    var meta = data.meta;
    this.meta[endpoint] = meta;
  }

  handleFetchRoutesFailed(err) {
    this.isLoading = false;
    this.lastErr = err;
  }

  handleFetchMetaFailed(data) {
    var endpoint = data.endpoint;
    var err = data.err;
    this.meta[endpoint] = {err: err};
  }

  getRoutes() {
    return this.routes;
  }

  isRoutesLoading() {
    return this.isLoading;
  }

  getMeta(route) {
    var { meta } = this.getState();
    var result = meta[route]
    return result;
  }
}

module.exports = alt.createStore(TableListStore, 'TableListStore');

