var React = require('react/addons'),
    ThemeManager = require('material-ui/lib/styles/theme-manager')(),
    AltContainer = require('alt/AltContainer'),
    TableListActions = require('./actions/TableListActions.jsx'),
    TableListStore = require('./stores/TableListStore'),
    FixedLeftNav = require('./components/FixedLeftNav.jsx'),
    Mui = require('material-ui'),
    {
      RouteHandler,
    } = require('react-router'),
    {
      AppBar,
      AppCanvas,
      LeftNav,
      Paper,
      RaisedButton,
    } = Mui,
    {
      Colors,
    } = Mui.Styles,
    {
      StylePropable,
      StyleResizable,
    } = Mui.Mixins,
    {
      ColorManipulator
    } = Mui.Utils;

var App = React.createClass({
  mixins: [StyleResizable, StylePropable],

  childContextTypes: {
    muiTheme: React.PropTypes.object
  },

  getChildContext: function() {
    return {
      muiTheme: ThemeManager.getCurrentTheme()
    };
  },

  componentWillMount: function() {
    this._setTheme();
  },

  componentDidMount: function() {
    TableListActions.fetchRoutes();
  },

  getStyles: function() {
    var theme = ThemeManager.getCurrentTheme();
    var Spacing = theme.spacing;
    var Palette = theme.palette;

    var largeNavWidth = Spacing.desktopKeylineIncrement * 3 + 'px';
    var windowWidth = typeof window !== 'undefined' ? window.innerWidth : 768

    var styles = {
      root: {},
      rootLarge: {
        position: 'relative',
        width: '100%'
      },
      nav: {
        borderTop: "solid 1px " + Palette.borderColor,
        overflow: "hidden",
      },
      navLarge: {
        borderTop: 'none',
        position: 'absolute',
        top: theme.component.appBar.height,
        width: largeNavWidth
      },
      content: {
        boxSizing: 'border-box',
        padding: Spacing.desktopGutter + 'px',
        maxWidth: windowWidth + 'px',
        backgroundColor: Palette.canvasColor
      },
      contentLarge: {
        marginLeft: largeNavWidth,
        maxWidth: (windowWidth - largeNavWidth) + 'px',
        borderLeft: 'solid 1px ' + Palette.borderColor,
        minHeight: '800px'
      }
    };

    if (this.isDeviceSize(StyleResizable.statics.Sizes.MEDIUM) || this.isDeviceSize(StyleResizable.statics.Sizes.LARGE)) {
      styles.root = this.mergeStyles(styles.root, styles.rootLarge);
      styles.nav = this.mergeStyles(styles.nav, styles.navLarge);
      styles.content = this.mergeStyles(styles.content, styles.contentLarge);
    }
    return styles;
  },

  render: function() {
    var styles = this.getStyles();
    var icon = <a href='/'><img src='/civitas_logo-blue.png'/></a>

    return (
      <Paper id='app-root' style={styles.root}>
        <AppBar showMenuIconButton={true} 
                iconElementLeft={icon}
                title='Civitas Intervention Science'/>
        <AltContainer store={TableListStore}>
          <FixedLeftNav style={styles.nav}/>
        </AltContainer>
        <Paper zDepth={0} style={styles.content}>
          <RouteHandler />
        </Paper>
      </Paper>
    );
  },

  _setTheme: function() {
    ThemeManager.setPalette({
      primary1Color: "#063156",
      accent1Color: "#00acf2",
      canvasColor: Colors.grey500
    });

    var palette = ThemeManager.getCurrentTheme().palette;
    ThemeManager.setPalette({
      primary2Color: ColorManipulator.darken(palette.primary1Color, 0.15),
      primary3Color: ColorManipulator.lighten(palette.primary1Color, 0.15),
      accent2Color: ColorManipulator.darken(palette.accent1Color, 0.15),
      accent3Color: ColorManipulator.lighten(palette.accent1Color, 0.15)
    });

    ThemeManager.setComponentThemes({
      table: {
        backgroundColor: Colors.white
      },
      tableHeader: {
        borderColor: palette.borderColor
      },
      tableHeaderColumn: {
        textColor: Colors.lightBlack
      },
      tableFooter: {
        borderColor: palette.borderColor,
        textColor: Colors.lightBlack
      },
      tableRow: {
        hoverColor: Colors.grey200,
        stripeColor: ColorManipulator.lighten(palette.primary1Color, 0.55),
        selectedColor: Colors.grey300,
        textColor: Colors.darkBlack,
        borderColor: palette.borderColor
      }
    });
  }
});

module.exports = App;

