var React = require('react/addons'),
    {
      DefaultRoute,
      Route,
      run,
    } = require('react-router'),

    App = require('../App'),
    Home = require('../Home'),
    About = require('../About'),
    TableView = require('../TableView');

var routes =
  <Route path='/' handler={App}>
    <Route path='about' handler={About}/>
    <Route name='table-display' path='table/:endpoint' handler={TableView}/>
    <DefaultRoute handler={Home}/>
  </Route>

module.exports = {
  routes: routes,
  prerender: (app) => {
    app.use((req, res) => {
      run(routes, req.url, (Handler) => {
        var html = React.renderToString(<Handler />);
        res.render('index.ejs', {reactOutput: html});
      });
    });
  }
}

