var React = require('react/addons'),
    Mui = require('material-ui'),
    {
      Menu,
      MenuItem,
      Paper,
    } = Mui,
    {
      StylePropable,
      StyleResizable,
    } = Mui.Mixins,

    TableListStore = require('../stores/TableListStore'),
    Spinner = require('./Spinner');

var FixedLeftNav = React.createClass({
  mixins: [StyleResizable, StylePropable],

  contextTypes: {
    router: React.PropTypes.func,
    muiTheme: React.PropTypes.object
  },

  propTypes: {
    routes: React.PropTypes.object,
    meta: React.PropTypes.object,
    lastErr: React.PropTypes.any,
    isLoading: React.PropTypes.bool
  },

  getDefaultProps: function() {
    return {
      routes: {},
      meta: {},
      lastErr: null,
      isLoading: false
    };
  },

  getStyles: function() {
    var Spacing = this.context.muiTheme.spacing;
    var Colors = this.context.muiTheme.palette;

    var lineHeight = this.context.muiTheme.spacing.desktopLeftNavMenuItemHeight;
    var styles = {
      root: {
        borderTop: "solid 1px " + Colors.borderColor,
        overflow: "hidden",
        minHeight: '800px',
        zIndex: 10
      },
      menu: {
        overflowY: 'auto',
        overflowX: 'hidden',
        height: '100%'
      },
      menuItem: {
        height: lineHeight,
        lineHeight: lineHeight + 'px',
        fontWeight: 200
      },
      menuItemLink: {
        height: lineHeight,
        lineHeight: lineHeight + 'px',
        display: 'block',
        textDecoration: 'none',
        color: this.context.muiTheme.palette.textColor
      },
      menuItemSubheader: { 
        height: this.context.muiTheme.spacing.desktopLeftNavMenuItemHeight,
        lineHeight: this.context.muiTheme.spacing.desktopLeftNavMenuItemHeight + 'px',
        overflow: 'hidden'
      }
    };

    var medium_extensions = {
      root: {
        borderTop: 'none',
        position: 'absolute',
        top: this.context.muiTheme.component.appBar.height,
        width: Spacing.desktopKeylineIncrement * 3 + 'px'
      }
    };

    if (this.isDeviceSize(StyleResizable.statics.Sizes.MEDIUM) || this.isDeviceSize(StyleResizable.statics.Sizes.LARGE)) {
      styles = this.mergeStyles(styles, medium_extensions);
    }
    return styles
  },

  render: function() {
    var styles = this.getStyles();
    return (
      <Paper style={this.mergeAndPrefix(styles.root)} zDepth={2}>
        {this._getMenu()}
      </Paper>
    );
  },

  _getMenu: function() {
    var styles = this.getStyles();
    if (this.props.isLoading) {
      return <Spinner />;
    } else {
      return <Menu
          ref='menuItems'
          zDepth={0}
          style={this.mergeAndPrefix(styles.menu)}
          menuItems={this._getMenuItems()}
          menuItemStyle={this.mergeAndPrefix(styles.menuItem)}
          menuItemStyleLink={this.mergeAndPrefix(styles.menuItemLink)}
          menuItemStyleSubheader={this.mergeAndPrefix(styles.menuItemSubheader)}
          selectedIndex={this._getSelectedIndex()}
          onItemClick={this._onMenuItemClick} />;
    }
  },

  _getMenuItems: function() {
    var menuItems = [
      {
        type: MenuItem.Types.SUBHEADER,
        text: "Data Tables"
      }
    ];
    var entryNum = 0;
    for (var resource in this.props.routes) {
      var endpoint = this.props.routes[resource];
      entryNum++;
      var menuItem = {
        payload: entryNum,
        text: this.props.meta[endpoint] ? this.props.meta[endpoint].name : resource,
        route: '/table/' + endpoint
      };
      menuItems.push(menuItem);
    }
    return menuItems;
  },

  _getSelectedIndex: function() {
    var menuItems = this._getMenuItems();

    if (!menuItems.length) {
      return;
    }

    for (var i = menuItems.length - 1; i >= 0; i--) {
      var currentItem = menuItems[i];
      if (currentItem.route && this.context.router.isActive(currentItem.route))
        return i;
    }
  },

  _onMenuItemClick: function(e, index, item) {
    this.context.router.transitionTo(item.route);
  }

});

module.exports = FixedLeftNav;

