    return <Table
      columnOrder={this.props.columnOrder}
      headerColumns={this.state.headerColumns}
      rowData={this.state.data} 
      footerColumns={{}}
      displaySelectAll={false} 
      showRowHover={true} 
      showRowSelectCheckbox={false} 
      selectable={false} />;
  }
});

module.exports = Home

