var AltContainer = require('alt/AltContainer'),
    React = require('react'),
    RawTableStore = require('./stores/RawTableStore'),
    RawTableActions = require('./actions/RawTableActions'),
    TableListStore = require('./stores/TableListStore'),
    TableDisplay = require('./components/TableDisplay');


class TableView extends React.Component {
  componentDidMount() {
    RawTableActions.fetchTable(this.props.params.endpoint);
  }

  componentWillReceiveProps(props) {
    var ep = props.params.endpoint;
    if (ep !== this.props.params.endpoint) {
      RawTableActions.fetchTable(ep);
    }
  }

  render() {
    var metadata = this._getMetaData();
    var stores = {
      title: (props) => {
        return {
          store: TableListStore,
          value: metadata.title
        };
      },
      columnOrder: (props) => {
        return {
          store: TableListStore,
          value: metadata.columnOrder
        };
      },
      headerColumns: (props) => {
        return {
          store: TableListStore,
          value: metadata.headerColumns
        };
      },
      rowData: (props) => {
        return {
          store: RawTableStore,
          value: RawTableStore.getData()
        };
      },
      isLoading: (props) => {
        return {
          store: RawTableStore,
          value: RawTableStore.isLoading()
        };
      },
    };

    return (
      <AltContainer stores={stores}>
        <TableDisplay />
      </AltContainer>
    );
  }

  _getMetaData() {
    var meta = TableListStore.getMeta(this.props.params.endpoint);
    var headerColumns = {};
    var columnOrder = [];
    for (var field in meta.ownedFields) {
      var desc = meta.ownedFields[field];
      columnOrder.push(field);
      headerColumns[field] = {
        content: desc['display_name'],
        tooltip: desc['description']
      };
    }
    return {
      columnOrder: columnOrder,
      headerColumns: headerColumns,
      title: meta.name,
    };
  }
}

module.exports = TableView;

