var React = require('react/addons'),
    {
      StylePropable,
    } = require('material-ui').Mixins,
    Router = require('react-router'),

    {Table} = require('./Table'),
    {
      Card,
      CardHeader,
      CardText,
    } = require('./Card'),
    Spinner = require('./Spinner');

var TableDisplay = React.createClass({
  mixins: [StylePropable],

  propTypes: {
    title: React.PropTypes.string,
    columnOrder: React.PropTypes.array,
    headerColumns: React.PropTypes.object,
    rowData: React.PropTypes.array,
    isLoading: React.PropTypes.bool,
  },

  render: function() {
    return <Card>
            <CardHeader title="Raw Data View" subtitle={this.props.title}/>
            <CardText>
              {this._getTable()}
            </CardText>
          </Card>;
  },

  _getTable: function() {
    if (this.props.isLoading) {
      return <Spinner />;
    } else {
      return <Table
        columnOrder={this.props.columnOrder}
        headerColumns={this.props.headerColumns}
        rowData={this.props.rowData} 
        displaySelectAll={false} 
        showRowHover={true} 
        showRowSelectCheckbox={false} 
        selectable={false} />;
    }
  }
});

module.exports = TableDisplay;

