var alt = require('../alt'),
    xhr = require('xhr'),
    {apiRoot} = require('../../settings');

class TableListActions {
  fetchRoutes() {
    this.dispatch();
  }

  updateRoutes(routes) {
    this.dispatch(routes);
  }

  updateMeta(data) {
    this.dispatch(data);
  }

  fetchRoutesFailed(err) {
    this.dispatch(err);
  }

  fetchMeta(endpoint) {
    this.dispatch(endpoint);
  }

  fetchRoutesLoading() {
    this.dispatch();
  }

  fetchMetaFailed(data) {
    this.dispatch(data);
  }
}

module.exports = alt.createActions(TableListActions);

