var React = require('react/addons'),
    {
      StylePropable,
    } = require('material-ui').Mixins;


var Spinner = React.createClass({
  mixins: [StylePropable],

  propTypes: {
    sizePx: React.PropTypes.number,
    durationSec: React.PropTypes.number,
  },

  getDefaultProps: function() {
    return {
      sizePx: 65,
      durationSec: 1.4
    };
  },

  getStyles: function() {
    return {
      spinner: {
        animation: 'rotator ' + this.props.durationSec + ' linear infinite',
      },
      path: {
        strokeDasharray: 187,
        strokeDashoffset: 0,
        transformOrigin: "center",
        animation: (
          "dash " + this.props.durationSec + " ease-in-out infinite, " +
          "colors (" + this.props.durationSec + "*4) ease-in-out infinite"
        )
      }
    };
  },

  render: function() {
    var vbSize = this.props.sizePx + 1;
    return (
      <svg width={this.props.sizePx}
           height={this.props.sizePx}
           style={this.getStyles().spinner}
           viewBox={"0 0 " + vbSize + " " + vbSize}>
        <circle fill="none"
                strokeWidth={Math.round(vbSize/11)}
                strokeLinecap="round"
                style={this.getStyles().path}
                cx={Math.round(vbSize/2)}
                cy={Math.round(vbSize/2)}
                r={Math.round(vbSize/2.2)}>
        </circle>
      </svg>
    );
  }
});

module.exports = Spinner;

