'use strict';
const {
        DefaultRoute,
        Route,
        Router,
        run,
      } = require('react-router');

const alt = require('../alt'),
      App = require('../App'),
      Home = require('../Home'),
      About = require('../About'),
      TableView = require('../TableView'),
      TableListActions = require('../actions/TableListActions');

const routes =
  <Route path='/' handler={App}>
    <Route path='about' handler={About}/>
    <Route name='table-display' path='table/:endpoint' handler={TableView}/>
    <DefaultRoute handler={Home}/>
  </Route>;

module.exports = {
  routes: routes,
  prerender: (app) => {
    app.use((req, res) => {
      TableListActions.fetchRoutes();
      const handle = new Promise((resolve) => {
        resolve(run(routes, req.url));
      });

      (async () => {
        const body = await alt.render(handle);

        console.log(body);
        res.render('index.ejs', { reactOutput: body });
      })();
    });
  }
};

