'use strict';
import React from 'react/addons';
import themeManagerFn from 'material-ui/lib/styles/theme-manager';
import { RouteHandler } from 'react-router';
import { AppBar, Paper, Styles, Mixins, Utils } from 'material-ui';
import AltContainer from 'alt/AltContainer';
const { StylePropable, StyleResizable } = Mixins;
const { ColorManipulator } = Utils;

import FixedLeftNav from './components/FixedLeftNav';

const ThemeManager = themeManagerFn();

export default React.createClass({
  mixins: [ StyleResizable, StylePropable ],

  propTypes: {
    flux: React.PropTypes.object
  },

  childContextTypes: {
    muiTheme: React.PropTypes.object
  },

  getChildContext () {
    return {
      muiTheme: ThemeManager.getCurrentTheme()
    };
  },

  componentWillMount () {
    this._setTheme();
  },

  getStyles () {
    const theme = ThemeManager.getCurrentTheme();
    const Spacing = theme.spacing;
    const Palette = theme.palette;

    const styles = {
      root: {},
      rootLarge: {
        position: 'relative',
        width: '100%'
      },
      nav: {
        borderTop: 'solid 1px ' + Palette.borderColor,
        overflow: 'hidden'
      },
      navLarge: {
        borderTop: 'none',
        position: 'absolute',
        width: Spacing.desktopKeylineIncrement * 3 + 'px'
      },
      content: {
        boxSizing: 'border-box',
        padding: Spacing.desktopGutter + 'px',
        backgroundColor: Palette.canvasColor,
        height: '100%'
      },
      contentLarge: {
        borderLeft: 'solid 1px ' + Palette.borderColor,
        minHeight: '800px',
        marginLeft: Spacing.desktopKeylineIncrement * 3 + 'px'
      }
    };

    if (this.isDeviceSize(StyleResizable.statics.Sizes.MEDIUM) || this.isDeviceSize(StyleResizable.statics.Sizes.LARGE)) {
      styles.root = this.mergeStyles(styles.root, styles.rootLarge);
      styles.nav = this.mergeStyles(styles.nav, styles.navLarge);
      styles.content = this.mergeStyles(styles.content, styles.contentLarge);
    }
    return styles;
  },

  render () {
    const styles = this.getStyles();
    const icon = <a href='/'><img src='/civitas_logo-blue.png'/></a>;

    return (
      <Paper id='app-root' style={styles.root} >
        <AppBar showMenuIconButton={true}
                iconElementLeft={icon}
                title='Civitas Intervention Science' />
        <AltContainer store={this.props.flux.getStore('institution-list')}>
          <FixedLeftNav style={styles.nav} {...this.props} />
        </AltContainer>
        <Paper zDepth={0} style={styles.content}>
          <RouteHandler {...this.props} />
        </Paper>
      </Paper>
    );
  },

  _setTheme () {
    ThemeManager.setPalette({
      primary1Color: '#063156',
      accent1Color: '#00acf2',
      canvasColor: Styles.Colors.grey500
    });

    const palette = ThemeManager.getCurrentTheme().palette;

    ThemeManager.setPalette({
      primary2Color: ColorManipulator.darken(palette.primary1Color, 0.15),
      primary3Color: ColorManipulator.lighten(palette.primary1Color, 0.15),
      accent2Color: ColorManipulator.darken(palette.accent1Color, 0.15),
      accent3Color: ColorManipulator.lighten(palette.accent1Color, 0.15)
    });

    ThemeManager.setComponentThemes({
      table: {
        backgroundColor: Styles.Colors.white
      },
      tableHeader: {
        borderColor: palette.borderColor
      },
      tableHeaderColumn: {
        textColor: Styles.Colors.lightBlack
      },
      tableFooter: {
        borderColor: palette.borderColor,
        textColor: Styles.Colors.lightBlack
      },
      tableRow: {
        hoverColor: Styles.Colors.grey200,
        stripeColor: ColorManipulator.lighten(palette.primary1Color, 0.55),
        selectedColor: Styles.Colors.grey300,
        textColor: Styles.Colors.darkBlack,
        borderColor: palette.borderColor
      }
    });
  }
});

