'use strict';
import React from 'react/addons';
import Alt from 'alt';
import Iso from 'iso';
import chromeDebug from 'alt/utils/chromeDebug';
import debug from 'debug';

import stores from './stores';
import actions from './actions';

class Flux extends Alt {
  constructor (config = {}) {
    super(config);

    for (let actionName in actions) {
      if (actions.hasOwnProperty(actionName)) {
        this.addActions(actionName, actions[actionName]);
      }
    }

    for (let storeName in stores) {
      if (stores.hasOwnProperty(storeName)) {
        this.addStore(storeName, stores[storeName]);
      }
    }

    this._toResolve = [];
  }

  resolve (promise) {
    /*eslint no-process-env:0*/
    if (typeof window !== 'undefined') {
      return new Promise(promise);
    }
    this._toResolve.push(new Promise(promise));
  }

  async render (Handler) {
    if (typeof window !== 'undefined') {
      return null;
    }
    let content;

    try {
      React.renderToString(React.createElement(Handler, { flux: this }));

      const maxDepth = 5;
      let promises;

      for (let depth = 1; depth < maxDepth; depth++) {
        promises = this._toResolve.slice();
        this._toResolve = [];
        await Promise.all(promises);
      }

      const app = React.renderToString(React.createElement(Handler, { flux: this }));

      content = Iso.render(app, this.flush());
    } catch (error) {
      debug('dev')(error.stack);
      content = '404';
    }

    return content;
  }
}

const alt = new Flux();

chromeDebug(alt);

export default alt;

module.exports = {
  apiRoot: 'http://localhost:8000/api/'
};

