'use strict';
import React from 'react/addons';
import {
         Menu,
         MenuItem,
         Mixins,
         Paper
       } from 'material-ui';
const {
        StylePropable,
        StyleResizable
      } = Mixins;

import Spinner from './Spinner';

export default React.createClass({
  mixins: [ StyleResizable, StylePropable ],

  contextTypes: {
    router: React.PropTypes.func,
    muiTheme: React.PropTypes.object,
    flux: React.PropTypes.object
  },

  propTypes: {
    currentPage: React.PropTypes.array,
    lastErr: React.PropTypes.any,
    isLoading: React.PropTypes.bool
  },

  getDefaultProps () {
    return {
      currentPage: [],
      lastErr: null,
      isLoading: false
    };
  },

  componentWillMount () {
    if (this.props.currentPage.length === 0) {
      this.context.flux.getActions('institution-list').fetch();
    }
  },

  getStyles () {
    const Spacing = this.context.muiTheme.spacing;
    const Colors = this.context.muiTheme.palette;

    const lineHeight = this.context.muiTheme.spacing.desktopLeftNavMenuItemHeight;
    let styles = {
      root: {
        borderTop: 'solid 1px ' + Colors.borderColor,
        overflow: 'hidden',
        zIndex: 10
      },
      menu: {
        overflowY: 'auto',
        overflowX: 'hidden',
        height: '100%'
      },
      menuItem: {
        height: lineHeight,
        lineHeight: lineHeight + 'px',
        fontWeight: 200
      },
      menuItemLink: {
        height: lineHeight,
        lineHeight: lineHeight + 'px',
        display: 'block',
        textDecoration: 'none',
        color: this.context.muiTheme.palette.textColor
      },
      menuItemSubheader: {
        height: this.context.muiTheme.spacing.desktopLeftNavMenuItemHeight,
        lineHeight: this.context.muiTheme.spacing.desktopLeftNavMenuItemHeight + 'px',
        overflow: 'hidden'
      }
    };

    const mediumExtensions = {
      root: {
        borderTop: 'none',
        position: 'absolute',
        height: 'calc(100% - ' + this.context.muiTheme.component.appBar.height + 'px)',
        top: this.context.muiTheme.component.appBar.height,
        width: Spacing.desktopKeylineIncrement * 3 + 'px'
      }
    };

    if (this.isDeviceSize(StyleResizable.statics.Sizes.MEDIUM) || this.isDeviceSize(StyleResizable.statics.Sizes.LARGE)) {
      styles = this.mergeStyles(styles, mediumExtensions);
    }
    return styles;
  },

  render () {
    const styles = this.getStyles();

    return (
      <Paper style={this.mergeAndPrefix(styles.root)} zDepth={2}>
        {this._getMenu()}
      </Paper>
    );
  },

  _getMenu () {
    const styles = this.getStyles();

    if (!this.props.currentPage && this.props.isLoadingRoutes) {
      return <Spinner />;
    }
    return <Menu
        ref='menuItems'
        zDepth={0}
        style={this.mergeAndPrefix(styles.menu)}
        menuItems={this._getMenuItems()}
        menuItemStyle={this.mergeAndPrefix(styles.menuItem)}
        menuItemStyleLink={this.mergeAndPrefix(styles.menuItemLink)}
        menuItemStyleSubheader={this.mergeAndPrefix(styles.menuItemSubheader)}
        selectedIndex={this._getSelectedIndex()}
        onItemClick={this._onMenuItemClick} />;
  },

  _getMenuItems () {
    const menuItems = [
      {
        type: MenuItem.Types.SUBHEADER,
        text: 'Institutions'
      }
    ];
    let entryNum = 0;

    for (let institution of this.props.currentPage) {
      const menuItem = {
        payload: entryNum,
        text: institution.name,
        route: '/institution/' + institution._id
      };

      entryNum++;
      menuItems.push(menuItem);
    }
    return menuItems;
  },

  _getSelectedIndex () {
    const menuItems = this._getMenuItems();

    if (!menuItems.length) {
      return null;
    }

    for (let i = menuItems.length - 1; i >= 0; i--) {
      const currentItem = menuItems[i];

      if (currentItem.route && this.context.router.isActive(currentItem.route)) {
        return i;
      }
    }
    return null;
  },

  _onMenuItemClick (e, index, item) {
    this.context.router.transitionTo(item.route);
  }

});

