var alt = require('../alt'),
    request = require('superagent'),

    Settings = require('../../settings'),
    RawTableActions = require('../actions/RawTableActions');

class RawTableStore {
  constructor() {
    this.data = [];
    this.lastErr = null;
    this.isLoading = false;

    this.bindListeners({
      handleFetchTable: RawTableActions.FETCH_TABLE,
      handleUpdateTable: RawTableActions.UPDATE_TABLE,
      handleFailedFetch: RawTableActions.FAILED_UPDATE,
      handleFetchLoading: RawTableActions.FETCH_LOADING
    });

    this.exportPublicMethods({
      getData: this.getData,
      isLoading: this.getIsLoading
    });
  }

  handleFetchTable(endpoint) {
    this.isLoading = true;
    request
      .get(Settings.apiRoot + endpoint + '/')
      .end((err, res) => {
        if (res.statusCode >= 400) {
          RawTableActions.failedFetch(body);
        }

        var rowData = [];
        for (var rowNum = 0; rowNum < res.body['results'].length; rowNum++) {
          var row = res.body['results'][rowNum];
          var dataRow = {};
          for (var col in row) {
            dataRow[col] = {
              content: row[col]
            };
          }
          rowData.push(dataRow);
        }
        RawTableActions.updateTable(rowData);
      }
    );
  }

  handleUpdateTable(data) {
    this.isLoading = false;
    this.data = data;
  }

  handleFailedFetch(err) {
    this.isLoading = false;
    this.lastErr = err;
  }

  handleFetchLoading() {
    this.data = [];
    this.isLoading = true;
  }

  getData() {
    return this.getState().data;
  }

  getIsLoading() {
    return this.getState().isLoading;
  }
}

module.exports = alt.createStore(RawTableStore, 'RawTableStore');

