'use strict';
import React from 'react/addons';
import { Mixins, DropDownMenu, Toolbar, ToolbarGroup } from 'material-ui';

import { Table } from './Table';
import {
         Card,
         CardHeader,
         CardText
       } from './Card';
import Spinner from './Spinner';

export default React.createClass({
  mixins: [ Mixins.StylePropable ],

  propTypes: {
    flux: React.PropTypes.object,
    currentTable: React.PropTypes.string,
    title: React.PropTypes.string,
    columnOrder: React.PropTypes.array,
    headerColumns: React.PropTypes.object,
    rowData: React.PropTypes.array,
    isLoading: React.PropTypes.bool,
    availableTables: React.PropTypes.object
  },

  componentWillMount () {
    if (!this.props.rowData.length) {
      let table = this.props.currentTable;

      if (!table && this.props.availableTables.length) {
        table = this.props.availableTables[0].endpoint;
      }
      if (table) {
        this.props.flux.getActions('data-table').fetchTable(table);
      }
    }
  },

  render () {
    const avatar = <img src='/database.png'/>;

    return <Card>
      <CardHeader avatar ={avatar} title='Raw Data View' subtitle={this.props.title}/>
        <CardText>
          <Toolbar>
            <ToolbarGroup key={0} float='right'>
              <DropDownMenu menuItems={this.props.availableTables}
                            onChange={this._onTableChange} />
            </ToolbarGroup>
          </Toolbar>
          {this._getTable()}
        </CardText>
      </Card>;
  },

  _getTable () {
    if (!this.props.rowData && this.props.isLoading) {
      return <Spinner />;
    }
    return <Table
      columnOrder={this.props.columnOrder}
      headerColumns={this.props.headerColumns}
      rowData={this.props.rowData}
      displaySelectAll={false}
      showRowHover={true}
      style={{ bodyTable: { maxHeight: '640px', overflowY: 'scroll' }}}
      showRowSelectCheckbox={false}
      selectable={false} />;
  },

  _onTableChange (e, selectedIndex, menuItem) {
    this.flux.getActions('data-table').fetchTable(menuItem.payload);
  }
});

