'use strict';
import React from 'react';
import { Styles, Mixins } from 'material-ui';

export default React.createClass({
  mixins: [ Mixins.StylePropable ],

  propTypes: {
    avatar: React.PropTypes.element,
    title: React.PropTypes.string,
    titleColor: React.PropTypes.string,
    subtitle: React.PropTypes.string,
    subtitleColor: React.PropTypes.string
  },

  getDefaultProps () {
    return {
      titleColor: Styles.Colors.darkBlack,
      subtitleColor: Styles.Colors.lightBlack
    };
  },

  getStyles () {
    return {
      root: {
        height: '72px',
        padding: '16px',
        fontWeight: Styles.Typography.fontWeightMedium,
        boxSizing: 'border-box'
      },
      avatar: {
        marginRight: '16px',
        height: '40px',
        width: '40px',
        display: 'inline-block',
        verticalAlign: 'top',
        borderRadius: '50%'
      },
      text: {
        display: 'inline-block',
        verticalAlign: 'top'
      },
      title: {
        color: this.props.titleColor,
        display: 'block',
        fontSize: '15px'
      },
      subtitle: {
        color: this.props.subtitleColor,
        display: 'block',
        fontSize: '14px'
      }
    };
  },

  render () {
    const styles = this.mergeStyles(this.getStyles(), this.props.style);
    const avatar = React.cloneElement(
      this.props.avatar,
      { style: styles.avatar }
    );

    return (
      <div style={styles.root}>
        {avatar}
        <div style={styles.text}>
          <span style={styles.title}>{this.props.title}</span>
          <span style={styles.subtitle}>{this.props.subtitle}</span>
        </div>
      </div>
    );
  }
});

