'use strict';
import React from 'react/addons';
import {
         DefaultRoute,
         Route,
         run
       } from 'react-router';

import alt from './alt';
import App from './App';
import Home from './Home';
import About from './About';
import InstitutionView from './InstitutionView';

const routes =
  <Route path='/' handler={App}>
    <Route path='about' handler={About}/>
    <Route name='institution-view' path='institution/:endpoint' handler={InstitutionView}/>
    <DefaultRoute handler={Home}/>
  </Route>;

module.exports = {
  routes,
  prerender: (app) => {
    app.use(function (req, res) {
      run(routes, req.url, function (Handler) {
        alt.render(Handler).then((body) => {
          res.render('index.ejs', { reactOutput: body });
        });
      });
    });
  }
};

