'use strict';

const patchedAutoPrefix = function (styles) {
  /** Material-ui's autoprefixer doesn't work on server-side and will break
   * isomorphic rendering. We'll just add every prefix to everything, all the
   * time.
   */
  const prefixes = [ '', '-webkit-', '-moz-', '-o-', '-ms-' ];
  const prefixedStyle = {};

  for (let key in styles) {
    if (styles.hasOwnProperty(key)) {
      for (let prefix in prefixes) {
        if (prefixes.hasOwnProperty(prefix)) {
          prefixedStyle[prefixes[prefix] + key] = styles[key];
        }
      }
    }
  }
  return prefixedStyle;
};

export default { patchedAutoPrefix };

