'use strict';
import request from 'superagent';
import Settings from '../../settings';

export default class InstitutionListStore {
  constructor () {
    this.currentPage = [];
    this._nextCursor = null;
    this._prevCursor = null;
    this.lastErr = null;
    this.loading = false;

    this.bindActions(this.alt.getActions('institution-list'));

    this.exportPublicMethods({
      getData: this.getData,
      getName: this.getName,
      hasNextPage: this.hasNextPage,
      hasPrevPage: this.hasPrevPage,
      isLoading: this.isLoading,
      getLastErr: this.getLastErr
    });
  }

  onFetch () {
    this._nextCursor = null;
    this._prevCursor = null;
    this.onNextPage();
  }

  onNextPage () {
    this.loading = true;
    this.alt.resolve(this._doFetch({ cursor: this._nextCursor }));
  }

  onPrevPage () {
    this.loading = true;
    this.alt.resolve(this._doFetch({ cursor: this._prevCursor }));
  }

  onUpdate (data) {
    const { next, previous, results } = data;

    this.loading = false;
    this._nextCursor = next;
    this._prevCursor = previous;
    this.currentPage = results;
  }

  onFetchFailed (err) {
    this.loading = false;
    this.lastErr = err;
  }

  getData () {
    return this.getState().currentPage;
  }

  getName (id) {
    for (let institution of this.getState().currentPage) {
      if (institution._id.toString() === id.toString()) {
        return institution.name;
      }
    }
    return '';
  }

  hasNextPage () {
    return !!this.getState()._nextCursor;
  }

  hasPrevPage () {
    return !!this.getState()._prevCursor;
  }

  isLoading () {
    return this.getState().loading;
  }

  getLastErr () {
    return this.getState().lastErr;
  }

  _doFetch (params={}) {
    return (resolve) => {
      request
        .get(Settings.apiRoot + 'institutions/')
        .query(params)
        .end((err, res) => {
          if (err) {
            this.alt.getActions('institution-list').fetchFailed(err);
          }

          this.alt.getActions('institution-list').update(res.body);
          return resolve();
        });
    };
  }
}

