'use strict';
import AltContainer from 'alt/AltContainer';
import React from 'react';
import TableDisplay from './components/TableDisplay';

export default class InstitutionView extends React.Component {
  propTypes: {
    flux: React.PropTypes.object
  }

  componentWillMount () {
    const current = this.props.flux.getStore('data-table').getCurrentEndpoint();

    if (this.props.params.endpoint !== current) {
      this.props.flux.getActions('data-table').fetchTable(this.props.params.endpoint);
    }
  }

  componentWillReceiveProps (props) {
    const ep = props.params.endpoint;

    if (ep !== this.props.params.endpoint) {
      this.props.flux.getActions('data-table').fetchTable(ep);
    }
  }

  render () {
    const metadata = this._getMetaData();
    const stores = {
      title: () => {
        return {
          store: this.props.flux.getStore('table-list'),
          value: metadata.title
        };
      },
      columnOrder: () => {
        return {
          store: this.props.flux.getStore('table-list'),
          value: metadata.columnOrder
        };
      },
      headerColumns: () => {
        return {
          store: this.props.flux.getStore('table-list'),
          value: metadata.headerColumns
        };
      },
      rowData: () => {
        return {
          store: this.props.flux.getStore('data-table'),
          value: this.props.flux.getStore('data-table').getData()
        };
      },
      isLoading: () => {
        return {
          store: this.props.flux.getStore('data-table'),
          value: this.props.flux.getStore('data-table').isLoading()
        };
      }
    };

    return (
      <AltContainer stores={stores}>
        <TableDisplay />
      </AltContainer>
    );
  }

  _getMetaData () {
    const meta = this.props.flux.getStore('table-list').getMeta(this.props.params.endpoint);

    if (meta === {}) {
      return {
        columnOrder: [],
        headerColumns: {},
        title: ''
      };
    }
    const headerColumns = {};
    const columnOrder = [];

    for (let field in meta.ownedFields) {
      if (meta.ownedFields.hasOwnProperty(field)) {
        const desc = meta.ownedFields[field];

        columnOrder.push(field);
        headerColumns[field] = {
          content: desc.display_name,
          tooltip: desc.description
        };
      }
    }

    return {
      columnOrder,
      headerColumns,
      title: meta.name
    };
  }
}

