sourcesContent:
'use strict';
import React from 'react';
import { Styles } from 'material-ui';

export default React.createClass({
  propTypes: {
    overlay: React.PropTypes.node
  },

  getStyles () {
    return {
      root: {
        position: 'relative'
      },
      overlayContainer: {
        position: 'absolute',
        top: '0',
        bottom: '0',
        right: '0',
        left: '0'
      },
      overlay: {
        height: '100%',
        position: 'relative'
      },
      overlayContent: {
        position: 'absolute',
        bottom: 0,
        right: 0,
        left: 0,
        paddingTop: '8px',
        background: Styles.Colors.lightBlack
      }
    };
  },

  render () {
    const styles = this.getStyles();

    const children = React.Children.map(this.props.children, function (child) {
      return React.cloneElement(child, {
        style: {
          verticalAlign: 'top',
          maxWidth: '100%'
        }
      });
    });

    const overlayChildren = React.Children.map(this.props.overlay, function (child) {
      if (child.type.displayName === 'CardHeader' ||
          child.type.displayName === 'CardTitle'
      ) {
        return React.cloneElement(child, {
          titleColor: Styles.Colors.darkWhite,
          subtitleColor: Styles.Colors.lightWhite
        });
      } else if (child.type.displayName === 'CardText') {
        return React.cloneElement(child, {
          color: Styles.Colors.darkWhite
        });
      }
      return child;
    });

    return (
      <div style={styles.root}>
        <div style={styles.media}>
          {children}
        </div>
        {(this.props.overlay) ?
          <div style={styles.overlayContainer}>
            <div style={styles.overlay}>
              <div style={styles.overlayContent}>
                {overlayChildren}
              </div>
            </div>
          </div> : ''}
      </div>
    );
  }
});

