'use strict';
import React from 'react/addons';
import Router from 'react-router/build/npm/lib';
import { Mixins } from 'material-ui';

import { TableHeader } from '../Table';
import TimelineElement from './TimelineElement';
import TimelineProgramLabel from './TimelineProgramLabel';

export default React.createClass({
  mixins: [ Mixins.StylePropable, Router.State ],

  contextTypes: {
    muiTheme: React.PropTypes.object
  },

  propTypes: {
    programList: React.PropTypes.array,
    termList: React.PropTypes.array
  },

  getDefaultProps () {
    return {
      /* eslint camelcase: 0 */
      styles: {}
    };
  },

  getStyles () {
    return {
      header: {
        backgroundColor: this.context.muiTheme.component.table.backgroundColor,
        padding: '0 ' + this.context.muiTheme.spacing.desktopGutter + 'px',
        width: '100%',
        borderCollapse: 'collapse',
        borderSpacing: 0,
        tableLayout: 'fixed'
      },
      programColumn: {
        textAlign: 'right',
        fontSize: 12,
        padding: 10,
        color: this.context.muiTheme.component.tableHeaderColumn.textColor,
        position: 'relative',
        whiteSpace: 'nowrap',
        fontWeight: 'normal'
      }
    };
  },

  render () {
    const { names, nums, rates } = this._formatTermInfo(),
          numByTerm = [],
          styles = this.mergeStyles(this.getStyles(), this.props.style),
          header = [],
          rows = [];

    for (let item of [ '' ].concat(names)) {
      header.push({ content: item });
    }

    for (let num of nums) {
      numByTerm.push(num);
    }
    const maxOverTerms = Math.max(...numByTerm);

    for (let progIdx = 0; progIdx < this.props.programList.length; progIdx++) {
      let programInfo = this.props.programList[progIdx];
      let columns = [
        <TimelineProgramLabel
          style={styles.programColumn}
          name={programInfo.display_name}
          description={programInfo.description}
          key={-1}/>
      ];

      for (let termIdx = 0; termIdx < names.length; termIdx++) {
        columns.push(
          <TimelineElement
            key={termIdx}
            termName={names[termIdx]}
            maxOverTerms={maxOverTerms}
            totalInTerm={nums[termIdx]}
            participationRate={rates[progIdx][termIdx]} />
        );
      }
      rows.push(<tr key={progIdx}>{columns}</tr>);
    }

    return <div>
      <div>
        <table ref='headerTable' style={styles.header}>
          <TableHeader
            columns={header}
            displaySelectAll={false}
            enableSelectAll={false} />
        </table>
      </div>
      <table style={styles.header}>
        {rows}
      </table>
    </div>;
  },

  _formatTermInfo () {
    const names = [],
          nums = [],
          rates = [];

    for (let idx = 0; idx < this.props.programList.length; idx++) {
      rates.push([]);
    }

    for (let term of this.props.termList) {
      names.push(term.civ_display_term);
      nums.push(term.numStudents);

      for (let idx = 0; idx < this.props.programList.length; idx++) {
        const { _id } = this.props.programList[idx];

        rates[idx].push(term.participationRate[_id]);
      }
    }

    return { names, nums, rates };
  }
});

