'use strict';
import React from 'react/addons';
import {
        Mixins
       } from 'material-ui';

import {
  Card,
  CardText
} from '../Card';
import IconCardHeader from '../IconCardHeader';
import Timeline from './Timeline';
import MaterialIcon from '../MaterialIcon';

export default React.createClass({
  mixins: [ Mixins.StylePropable ],

  contextTypes: {
    flux: React.PropTypes.object,
    router: React.PropTypes.func
  },

  render () {
    const avatar = <MaterialIcon name='trending_up'/>,
          instId = this.context.router.getCurrentParams().institution,
          instName = this.context.flux.getStore('institution-list').getName(instId),
          title = instName + ' Intervention Programs';

    return <Card>
      <IconCardHeader
        avatar={avatar}
        title="Timeline View"
        subtitle={title}/>
      <CardText>
        <Timeline {...this.props} />
      </CardText>
    </Card>;
  }
});

