'use strict';
import React from 'react/addons';
import { Mixins, Tooltip } from 'material-ui';

export default React.createClass({
  mixins: [ Mixins.StylePropable ],

  propTypes: {
    termName: React.PropTypes.string,
    maxOverTerms: React.PropTypes.number,
    totalInTerm: React.PropTypes.number,
    participationRate: React.PropTypes.number
  },

  getInitialState () {
    return { hovered: false };
  },

  getDefaultProps () {
    return {
      style: {}
    };
  },

  getStyles () {
    const saturation = Math.pow(this._numParticipants() / this.props.maxOverTerms, 1 / 2),
          desaturate = (x) => {
            return Math.round((255 * (1 - saturation)) + (x * saturation));
          },
          rgb = [ 8, 69, 117 ].map(desaturate).join(','),
          defaultStyle = {
            root: {
              height: '48px',
              width: '100%',
              backgroundColor: 'rgb(' + rgb + ')',
              borderRadius: '5px',
              margin: '2px',
              position: 'relative'
            },
            tooltip: {
              zIndex: 10,
              boxSizing: 'border-box'
            }
          };

    return this.mergeStyles(defaultStyle, this.props.style);
  },

  render () {
    const style = this.getStyles(),
          label = this.props.termName + ': ' + this._numParticipants() + ' Participants',
          handlers = {
            onMouseOver: this._onMouseOver,
            onMouseOut: this._onMouseOut
          };

    return <td style={{ position: 'relative' }} {...handlers} >
      <div style={style.root}/>
      <Tooltip
        style={style.tooltip}
        label={label}
        show={this.state.hovered} />
    </td>;
  },

  _onMouseOver () {
    this.setState({ hovered: true });
  },

  _onMouseOut () {
    this.setState({ hovered: false });
  },

  _numParticipants () {
    return Math.round(this.props.participationRate * this.props.totalInTerm);
  }
});

