'use strict';
import request from 'superagent';
import Settings from '../../settings';

export default class TableListStore {
  constructor () {
    this.routes = [];
    this.meta = {};
    this.lastErrRoutes = null;
    this.lastErrMeta = {};
    this.isLoadingRoutes = false;
    this.isLoadingMeta = {};

    this.bindActions(this.alt.getActions('table-list'));

    this.exportPublicMethods({
      getRoutes: this.getRoutes,
      getMeta: this.getMeta,
      isRoutesLoading: this.isRoutesLoading,
      isMetaLoading: this.isMetaLoading,
      getLastErrRoutes: this.getLastErrRoutes,
      getLastErrMeta: this.getLastErrMeta
    });
  }

  onFetchRoutes () {
    if (!this.isLoadingRoutes) {
      this.isLoadingRoutes = true;
      const promise = (resolve) => {
        request
          .get(Settings.apiRoot)
          .end((err, res) => {
            (async () => {
              if (err) {
                this.alt.getActions('table-list').fetchRoutesFailed(err);
              }

              let result = [];
              let resource = null;

              for (resource in res.body) {
                if (res.body.hasOwnProperty(resource) && resource !== 'institutions') {
                  const uri = res.body[resource];
                  const endpoint = uri.slice(Settings.apiRoot.length).replace(/\/$/, '');

                  result.push({ resource, endpoint });
                  await this._fetchMeta(endpoint);
                }
              }
              this.alt.getActions('table-list').updateRoutes(result);
              return resolve();
            })();
          }
        );
      };

      this.alt.resolve(promise);
    }
  }

  async _fetchMeta (endpoint) {
    if (!this.isLoadingMeta[endpoint]) {
      this.isLoadingMeta[endpoint] = true;
      const promise = new Promise((resolve) => {
        request('OPTIONS', Settings.apiRoot + endpoint + '/')
          .end((err, res) => {
            if (err) {
              this.alt.getActions('table-list').fetchMetaFailed(endpoint, err);
            }

            const data = res.body;

            this.alt.getActions('table-list').updateMeta({
              endpoint,
              meta: {
                name: data.name,
                description: data.description,
                ownedFields: data.owned_fields,
                relatedFields: data.related_fields
              }
            });
            return resolve();
          }
        );
      });

      return await promise;
    }
  }

  onUpdateRoutes (routes) {
    this.isLoadingRoutes = false;
    this.routes = routes;
  }

  onUpdateMeta (data) {
    const {endpoint, meta} = data;

    this.isLoadingMeta[endpoint] = false;
    this.meta[endpoint] = meta;
  }

  onFetchRoutesFailed (err) {
    this.isLoadingRoutes = false;
    this.lastErrRoutes = err;
  }

  onFetchMetaFailed (data) {
    const {err, endpoint} = data;

    this.isLoadingMeta[endpoint] = false;
    this.lastErrMeta[endpoint] = err;
  }

  getRoutes () {
    const { routes } = this.getState();

    return routes;
  }

  getMeta (route) {
    const { meta } = this.getState();

    if (route in meta) {
      return meta[route];
    }
    return {};
  }

  isRoutesLoading () {
    const { isLoadingRoutes } = this.getState();

    return isLoadingRoutes;
  }

  isMetaLoading (route) {
    const { isLoadingMeta } = this.getState();

    if (route in isLoadingMeta) {
      return isLoadingMeta[route];
    }
    return false;
  }

  getLastErrRoutes () {
    const { lastErrRoutes } = this.getState();

    return lastErrRoutes;
  }

  getLastErrMeta (route) {
    const { lastErrMeta } = this.getState();

    if (route in lastErrMeta) {
      return lastErrMeta[route];
    }
    return null;
  }
}

