'use strict';
import request from 'superagent';
import Settings from '../../settings';

export default class ProgramListStore {
  constructor () {
    this.programs = [];
    this.loading = false;
    this.lastErr = null;

    this.bindActions(this.alt.getActions('program-list'));
    this.bindAction(
      this.alt.getActions('institution').UPDATE,
      this.onUpdateInstitution
    );
  }

  onFetch (params) {
    if (!this.loading) {
      const query = params ? params : {},
            { institution } = this.alt.getStore('institution').getState();

      if (institution) {
        if (query.institution && query.institution !== institution) {
          console.warn('Overriding query parameter `institution` with current store value');
        }
        query.institution = institution;
      }

      const promise = (resolve) => {
        request
          .get(Settings.apiRoot + 'intervention_programs/')
          .query(query)
          .end((err, res) => {
            (async () => {
              if (err) {
                this.alt.getActions('program-list').fetchFailed(err);
              }
              this.alt.getActions('program-list').update(res.body.results);
              return resolve();
            })();
          }
        );
      };

      this.loading = true;
      this.alt.resolve(promise);
    }
  }

  onFetchFailed (err) {
    this.loading = false;
    this.lastErr = err;
  }

  onUpdate (programs) {
    this.loading = false;
    this.programs = programs;
  }

  onUpdateInstitution () {
    this.waitFor(this.alt.getStore('institution'));
    this.onFetch();
  }
}

