'use strict';
import React from 'react/addons';
import { RouteHandler } from 'react-router/build/npm/lib';
import { AppBar, Paper, Mixins } from 'material-ui';
import AltContainer from 'alt/AltContainer';
const { StylePropable, StyleResizable } = Mixins;

import FixedLeftNav from '../components/FixedLeftNav';
import muiTheme from '../styles/material-theme';

export default React.createClass({
  mixins: [ StyleResizable, StylePropable ],

  propTypes: {
    flux: React.PropTypes.object
  },

  childContextTypes: {
    muiTheme: React.PropTypes.object,
    flux: React.PropTypes.object
  },

  getChildContext () {
    return { muiTheme, flux: this.props.flux };
  },

  getStyles () {
    const Spacing = muiTheme.spacing;
    const Palette = muiTheme.palette;

    const styles = {
      root: {},
      rootLarge: {
        position: 'relative',
        width: '100%'
      },
      nav: {
        borderTop: 'solid 1px ' + Palette.borderColor,
        overflow: 'hidden'
      },
      navLarge: {
        borderTop: 'none',
        position: 'absolute',
        width: Spacing.desktopKeylineIncrement * 3 + 'px'
      },
      content: {
        boxSizing: 'border-box',
        padding: Spacing.desktopGutter + 'px',
        backgroundColor: Palette.canvasColor,
        height: '100%'
      },
      contentLarge: {
        borderLeft: 'solid 1px ' + Palette.borderColor,
        minHeight: '800px',
        marginLeft: Spacing.desktopKeylineIncrement * 3 + 'px'
      }
    };

    if (this.isDeviceSize(StyleResizable.statics.Sizes.MEDIUM) || this.isDeviceSize(StyleResizable.statics.Sizes.LARGE)) {
      styles.root = this.mergeStyles(styles.root, styles.rootLarge);
      styles.nav = this.mergeStyles(styles.nav, styles.navLarge);
      styles.content = this.mergeStyles(styles.content, styles.contentLarge);
    }
    return styles;
  },

  render () {
    const styles = this.getStyles();
    const icon = <a href='/'><img alt='Civitas Learning' height='48px' src='/Civitas_Logo.png'/></a>;

    return (
      <Paper id='app-root' style={styles.root} >
        <AppBar showMenuIconButton={true}
                iconElementLeft={icon}
                title='Intervention Science' />
        <AltContainer store={this.props.flux.getStore('institution-list')}>
          <FixedLeftNav style={styles.nav} />
        </AltContainer>
        <Paper zDepth={0} style={styles.content}>
          <RouteHandler />
        </Paper>
      </Paper>
    );
  }
});

