'use strict';
import React from 'react/addons';
import { Mixins } from 'material-ui';

export default React.createClass({
  mixins: [ Mixins.StylePropable ],

  propTypes: {
    sizePx: React.PropTypes.number,
    durationSec: React.PropTypes.number
  },

  getDefaultProps () {
    return {
      sizePx: 65,
      durationSec: 1.4
    };
  },

  getStyles () {
    const spinnerAnimation = 'rotator ' + this.props.durationSec + 's linear infinite';
    const pathAnimation = (
      'dash ' + this.props.durationSec + 's ease-in-out infinite, ' +
      'colors ' + this.props.durationSec * 4 + 's ease-in-out infinite'
    );

    return {
      spinner: {
        animation: spinnerAnimation,
        WebkitAnimation: spinnerAnimation
      },
      path: {
        strokeDasharray: 187,
        strokeDashoffset: 0,
        transformOrigin: 'center',
        WebkitTransformOrigin: 'center',
        animation: pathAnimation,
        WebkitAnimation: pathAnimation
      }
    };
  },

  render () {
    const vbSize = this.props.sizePx + 1;

