sourcesContent:
'use strict';
import themeManagerFn from 'material-ui/lib/styles/theme-manager';
import { Styles, Utils } from 'material-ui';
const ThemeManager = themeManagerFn(),
      { ColorManipulator } = Utils;

ThemeManager.contentFontFamily = 'Lato';
ThemeManager.setPalette({
  primary1Color: '#333333',
  accent1Color: '#00aeef',
  canvasColor: Styles.Colors.grey500
});

let palette = ThemeManager.getCurrentTheme().palette;

ThemeManager.setPalette({
  primary2Color: ColorManipulator.darken(palette.primary1Color, 0.15),
  primary3Color: ColorManipulator.lighten(palette.primary1Color, 0.15),
  accent2Color: ColorManipulator.darken(palette.accent1Color, 0.15),
  accent3Color: ColorManipulator.lighten(palette.accent1Color, 0.15)
});

palette = ThemeManager.getCurrentTheme().palette;

ThemeManager.setComponentThemes({
  dropDownMenu: {
    accentColor: ColorManipulator.darken(palette.borderColor, 0.15)
  },
  table: {
    backgroundColor: Styles.Colors.white
  },
  tableHeader: {
    borderColor: palette.borderColor
  },
  tableHeaderColumn: {
    textColor: Styles.Colors.lightBlack
  },
  tableFooter: {
    borderColor: palette.borderColor,
    textColor: Styles.Colors.lightBlack
  },
  tableRow: {
    hoverColor: Styles.Colors.grey200,
    stripeColor: ColorManipulator.lighten(palette.primary1Color, 0.55),
    selectedColor: Styles.Colors.grey300,
    textColor: Styles.Colors.darkBlack,
    borderColor: palette.borderColor
  }
});

export default ThemeManager.getCurrentTheme();

