import React from 'react/addons';
import { Mixins } from 'material-ui';

import { Table } from '../components/Table';

export default React.createClass({
  mixins: [ Mixins.StylePropable ],

  propTypes: {
    flux: React.PropTypes.object,
    columnOrder: React.PropTypes.array
  },

  getDefaultProps () {
    return {
      columnOrder: [
        'civ_gpa_at',
        'credits_earned_atsum_at',
        'civ_is_ftic_mr',
        'terms_completed_atsum_at'
      ]
    };
  },

  getInitialState () {
    return {
      data: [],
      headerColumns: {}
    };
  },

  render () {
    return <Table
      columnOrder={this.props.columnOrder}
      headerColumns={this.state.headerColumns}
      rowData={this.state.data}
      footerColumns={{}}
      displaySelectAll={false}
      showRowHover={true}
      showRowSelectCheckbox={false}
      selectable={false} />;
  }
});

