import React from 'react';
import { Tooltip, Mixins } from 'material-ui';
const { StylePropable } = Mixins;

export default React.createClass({

  mixins: [ StylePropable ],

  contextTypes: {
    muiTheme: React.PropTypes.object
  },

  propTypes: {
    tooltip: React.PropTypes.string,
    columnNumber: React.PropTypes.number.isRequired,
    onClick: React.PropTypes.func
  },

  getInitialState () {
    return {
      showToolip: false
    };
  },

  getTheme () {
    return this.context.muiTheme.component.tableHeaderColumn;
  },

  getStyles () {
    const styles = {
      root: {
        verticalAlign: 'bottom',
        fontWeight: 'normal',
        fontSize: 12,
        padding: 20,
        textAlign: 'left',
        whiteSpace: 'nowrap',
        textOverflow: 'ellipsis',
        color: this.getTheme().textColor,
        position: 'relative'
      }
    };

    return styles;
  },

  render () {
    const className = 'mui-table-header-column';
    const handlers = {
      onMouseOver: this._onMouseOver,
      onMouseOut: this._onMouseOut,
      onClick: this._onClick
    };
    let tooltip;

    if (this.props.tooltip !== undefined) {
      tooltip = (
        <Tooltip
          ref='tooltip'
          style={{ boxSizing: 'border-box' }}
          label={this.props.tooltip}
          show={this.state.showTooltip} />
      );
    }

    return (
      <th
        key={this.props.key}
        className={className}
        style={this.mergeAndPrefix(this.getStyles().root, this.props.style)}
        {...handlers}>
        {tooltip}
        {this.props.children}
      </th>
    );
  },

  _onMouseOver () {
    if (this.props.tooltip !== undefined) {
      this.setState({ showTooltip: true });
    }
  },

  _onMouseOut () {
    if (this.props.tooltip !== undefined) {
      this.setState({ showTooltip: false });
    }
  },

  _onClick (e) {
    if (this.props.onClick) {
      this.props.onClick(e, this.props.columnNumber);
    }
  }

});

