    onHover: React.PropTypes.func,
    onHoverExit: React.PropTypes.func,
    hoverable: React.PropTypes.bool
  },

  getDefaultProps () {
    return {
      hoverable: false
    };
  },

  getInitialState () {
    return {
      hovered: false
    };
  },

  getStyles () {
    const styles = {
      padding: 20,
      textAlign: 'left',
      fontSize: 13,
      overflow: 'auto',
      whiteSpace: 'nowrap',
      textOverflow: 'ellipsis',
      position: 'relative'
    };

    if (React.Children.count(this.props.children) === 1 && !isNaN(this.props.children)) {
      styles.textAlign = 'right';
    }

    return styles;
  },

  render () {
    const className = 'mui-table-row-column';
    const styles = this.getStyles();
    const handlers = {
      onClick: this._onColumnClick,
      onMouseOver: this._onMouseOver,
      onMouseOut: this._onMouseOut
    };
    let tooltip;

    if (this.props.tooltip !== undefined) {
      tooltip = (
        <Tooltip
          ref='tooltip'
          style={{ boxSizing: 'border-box' }}
          label={this.props.tooltip}
          show={this.state.hovered} />
      );
    }

    return (
      <td
        key={this.props.key}
        className={className}
        style={this.mergeAndPrefix(styles, this.props.style)}
        {...handlers}>
        {tooltip}
        {this.props.children}
      </td>
    );
  },

  _onColumnClick (e) {
    if (this.props.onColumnClick) {
      this.props.onColumnClick(e, this.props.columnNumber);
    }
