'use strict';
import AltContainer from 'alt/AltContainer';
import React from 'react';
import DataTableCard from '../components/DataTableCard';
import { TimelineCard } from '../components/Timeline';
import { AnalysisCard } from '../components/Analysis';

export default React.createClass({
  contextTypes: {
    flux: React.PropTypes.object,
    router: React.PropTypes.func
  },

  statics: {
    willTransitionTo: (e, params) => {
      e.context.flux.getActions('institution').update(params.institution);
    }
  },

  componentWillMount () {
    const { institution } = this.context.router.getCurrentParams();

    this.context.flux.getActions('institution').update(institution);
  },

  render () {
    const TableListStore = this.context.flux.getStore('table-list'),
          DataTableStore = this.context.flux.getStore('data-table'),
          ProgramListStore = this.context.flux.getStore('program-list'),
          ExperimentsStore = this.context.flux.getStore('experiments'),

          dataTableStores = {
            loadedEndpoint () {
              return {
                store: DataTableStore,
                value: DataTableStore.getCurrentEndpoint()
              };
            },
            meta () {
              return {
                store: TableListStore,
                value: TableListStore.getState().meta
              };
            }
          };

    return <div>
      <TimelineCard />
      <AltContainer stores={{ ProgramListStore, ExperimentsStore }}>
        <AnalysisCard />
      </AltContainer>
      <AltContainer stores={dataTableStores}>
        <DataTableCard />
      </AltContainer>
    </div>;
  }
});

