'use strict';
import React from 'react/addons';
import { Mixins } from 'material-ui';
import AltContainer from 'alt/AltContainer';

import {
         Card,
         CardMedia,
         CardText
       } from './Card';
import DataTableToolbar from './DataTableToolbar';
import DataTable from './DataTable';
import MaterialIcon from './MaterialIcon';
import IconCardHeader from './IconCardHeader';

export default React.createClass({
  mixins: [ Mixins.StylePropable ],

  contextTypes: {
    flux: React.PropTypes.object,
    muiTheme: React.PropTypes.object
  },

  propTypes: {
    loadedEndpoint: React.PropTypes.string,
    meta: React.PropTypes.object
  },

  render () {
    /* eslint object-shorthand: 0 */
    const avatar = <MaterialIcon name='storage' />,
          { loadedEndpoint, meta, ...props } = this.props,
          currentMeta = meta[loadedEndpoint],
          title = currentMeta ? currentMeta.name : '',

          DataTableStore = this.context.flux.getStore('data-table'),
          TableListStore = this.context.flux.getStore('table-list'),

          toolbarStores = {
            loadedEndpoint () {
              return {
                store: DataTableStore,
                value: DataTableStore.getCurrentEndpoint()
              };
            },
            routes () {
              return {
                store: TableListStore,
                value: TableListStore.getState().routes
              };
            },
            meta () {
              return {
                store: TableListStore,
                value: TableListStore.getState().meta
              };
            },
            isLoadingRoutes () {
              return {
                store: TableListStore,
                value: TableListStore.getState().isLoadingRoutes
              };
            }
          },

          dataTableStores = {
            loadedEndpoint () {
              return {
                store: DataTableStore,
                value: DataTableStore.getCurrentEndpoint()
              };
            },
            data () {
              return {
                store: DataTableStore,
                value: DataTableStore.getState().data
              };
            },
            lastErr () {
              return {
                store: DataTableStore,
                value: DataTableStore.getState().lastErr
              };
            },
            isLoading () {
              return {
                store: DataTableStore,
                value: DataTableStore.getState().isLoading
              };
            },
            isLoadingRoutes () {
              return {
                store: TableListStore,
                value: TableListStore.isRoutesLoading()
              };
            },
            meta () {
              return {
                store: TableListStore,
                value: TableListStore.getState().meta
              };
            }
          };

    return <Card style={{ minHeight: '480px' }} >
      <IconCardHeader avatar ={avatar} title='Raw Data View' subtitle={title}/>
      <CardMedia>
        <AltContainer stores={toolbarStores}>
          <DataTableToolbar />
        </AltContainer>
      </CardMedia>
      <CardText>
        <AltContainer stores={dataTableStores}>
          <DataTable />
        </AltContainer>
      </CardText>
    </Card>;
  }
});

