'use strict';
import React from 'react/addons';
import { Paper, Mixins, Styles } from 'material-ui';

export default React.createClass({
  mixins: [ Mixins.StylePropable ],

  propTypes: {
    zDepth: React.PropTypes.oneOf([ 0, 1, 2, 3, 4, 5 ])
  },

  getDefaultProps () {
    return {
      style: {}
    };
  },

  render () {
    const style = this.mergeStyles(
      {
        overflow: 'hidden',
        zIndex: 1,
        margin: (Styles.Spacing.desktopGutter / 2) + 'px'
      },
      this.props.style
    );
    let lastElement;

    if (React.Children.count(this.props.children) > 1) {
      lastElement = this.props.children[this.props.children.length - 1];
    } else {
      lastElement = this.props.children;
    }

    // If the last element is text or a title we should add
    // 8px padding to the bottom of the card
    const addBottomPadding = (lastElement.type.displayName === 'CardText' ||
                              lastElement.type.displayName === 'CardTitle');

    return (
      <Paper style={style}
             zDepth={this.props.zDepth}>
        <div style={{ paddingBottom: addBottomPadding ? '8px' : '0' }}>
          {this.props.children}
        </div>
      </Paper>
    );
  }
});

