'use strict';
import React from 'react/addons';
import { Mixins, DropDownMenu, Toolbar, ToolbarGroup } from 'material-ui';

export default React.createClass({
  mixins: [ Mixins.StylePropable ],

  contextTypes: {
    flux: React.PropTypes.object,
    muiTheme: React.PropTypes.object,
    router: React.PropTypes.func
  },

  propTypes: {
    loadedEndpoint: React.PropTypes.string,
    routes: React.PropTypes.array,
    meta: React.PropTypes.object,
    isLoadingRoutes: React.PropTypes.bool
  },

  getDefaultProps () {
    return {};
  },

  componentWillMount () {
    if (!this.props.routes.length) {
      this.context.flux.getActions('table-list').fetchRoutes();
    }
  },

  getStyles () {
    return {
    };
  },

  render () {
    const styles = this.mergeStyles(this.getStyles(), this.props.style);

    return <Toolbar>
      <ToolbarGroup key={0} float='right'>
        <DropDownMenu menuItems={this._getMenuItems()}
                      selectedIndex={this._getSelected()}
                      style={styles.dropdown}
                      onChange={this._onDropdownSelect} />
      </ToolbarGroup>
    </Toolbar>;
  },

  _getMenuItems () {
    if (!this.props.routes.length) {
      if (this.props.isLoadingRoutes) {
        return [
          { payload: null, text: 'Loading...' }
        ];
      } else if (this.props.lastErrRoutes) {
        return [
          {
            payload: null,
            text: 'Error (' + this.props.lastErrRoutes.status + ')'
          }
        ];
      }
      this.context.flux.getActions('table-list').fetchRoutes();
      return [{ payload: null, text: 'Loading...' }];
    }

    const items = [];
    let meta, name;

    for (let route of this.props.routes) {
      meta = this.props.meta[route.endpoint];
      name = meta ? meta.name : route.endpoint;
      items.push({ payload: route.endpoint, text: name });
    }
    return items;
  },

  _getSelected () {
    let endpoint;

    for (let idx = 0; idx < this.props.routes.length; idx++) {
      endpoint = this.props.routes[idx].endpoint;
      if (endpoint === this.props.loadedEndpoint) {
        return idx;
      }
    }
    return 0;
  },

  _onDropdownSelect (e, selectedIndex, menuItem) {
    this.context.flux.getActions('data-table').fetchTable({
      endpoint: menuItem.payload
    });
  }
});

