'use strict';
import React from 'react/addons';
import { Mixins } from 'material-ui';

import { Table } from './Table';
import Spinner from './Spinner';

export default React.createClass({
  mixins: [ Mixins.StylePropable ],

  propTypes: {
    loadedEndpoint: React.PropTypes.string,
    data: React.PropTypes.array,
    lastErr: React.PropTypes.object,
    isLoading: React.PropTypes.bool,
    isLoadingRoutes: React.PropTypes.bool,
    meta: React.PropTypes.object
  },

  getStyles () {
    return {
      root: {
        bodyTable: {
          maxHeight: '640px',
          overflowY: 'scroll'
        }
      }
    };
  },

  render () {
    const meta = this._getMetaData(),
          styles = this.mergeStyles(this.getStyles(), this.props.style);

    if (this.props.isLoading || this.props.isLoadingRoutes) {
      return <Spinner />;
    } else if (!Object.keys(meta).length) {
      const status = this.props.lastErr ? this.props.lastErr.status : 'unknown';

      return (
        <span>An error occured in fetching table data. Please refresh and try
          again. If this error persists, contact site administrators. Error code:
          {status}</span>
      );
    }

    return <Table
      columnOrder={meta.columnOrder}
      headerColumns={meta.headerColumns}
      rowData={this.props.data}
      displaySelectAll={false}
      showCellHover={true}
      showRowHover={true}
      style={styles.root}
      showRowSelectCheckbox={false}
      selectable={false} />;
  },

  _getMetaData () {
    if (!Object.keys(this.props.meta).length || !this.props.loadedEndpoint) {
      return {};
    }

    const headerColumns = {},
          columnOrder = [ '_id' ],
          meta = this.props.meta[this.props.loadedEndpoint];

    if (!meta) {
      return {};
    }

    for (let field in meta.relatedFields) {
      if (meta.relatedFields.hasOwnProperty(field)) {
        const desc = meta.relatedFields[field];

        columnOrder.push(field);
        headerColumns[field] = {
          content: desc.display_name,
          tooltip: desc.description
        };
      }
    }

    for (let field in meta.ownedFields) {
      if (meta.ownedFields.hasOwnProperty(field)) {
        const desc = meta.ownedFields[field];

        if (field !== 'id') {
          columnOrder.push(field);
        }
        headerColumns[field] = {
          content: desc.display_name,
          tooltip: desc.description
        };
      }
    }

    return {
      columnOrder,
      headerColumns,
      title: meta.name
    };
  }
});

