'use strict';
import React from 'react/addons';
import { Mixins, Tooltip } from 'material-ui';

export default React.createClass({
  mixins: [ Mixins.StylePropable ],

  propTypes: {
    name: React.PropTypes.string,
    description: React.PropTypes.string,
    progId: React.PropTypes.number
  },

  contextTypes: {
    flux: React.PropTypes.object
  },

  getInitialState () {
    return { hovered: false };
  },

  getDefaultProps () {
    return {
      style: {}
    };
  },

  render () {
    const handlers = {
      onMouseOver: this._onMouseOver,
      onMouseOut: this._onMouseOut,
      onClick: this._onClick
    };

    return <td style={this.props.style} {...handlers}>
      <Tooltip
        label={this.props.description}
        style={{ zIndex: 2, boxSizing: 'border-box' }}
        show={this.state.hovered}/>
      {this.props.name}
    </td>;
  },

  _onMouseOver () {
    this.setState({ hovered: true });
  },

  _onMouseOut () {
    this.setState({ hovered: false });
  },

  _onClick () {
    this.context.flux.getActions('active-program').update(this.props.progId);
  }
});

