sourcesContent:
'use strict';
import React from 'react/addons';
import {
        Mixins
       } from 'material-ui';
import AltContainer from 'alt/AltContainer';

import {
  Card,
  CardText
} from '../Card';
import IconCardHeader from '../IconCardHeader';
import Timeline from './Timeline';
import MaterialIcon from '../MaterialIcon';

export default React.createClass({
  mixins: [ Mixins.StylePropable ],

  contextTypes: {
    flux: React.PropTypes.object,
    router: React.PropTypes.func
  },

  render () {
    const avatar = <MaterialIcon name='trending_up'/>,
          instId = this.context.router.getCurrentParams().institution,
          instName = this.context.flux.getStore('institution-list').getName(instId),

          ProgramListStore = this.context.flux.getStore('program-list'),
          ActiveTermsStore = this.context.flux.getStore('active-terms'),

          title = instName + ' Intervention Programs',
          timelineStores = {
            programList () {
              return {
                store: ProgramListStore,
                value: ProgramListStore.getState().programs
              };
            },
            termList () {
              return {
                store: ActiveTermsStore,
                value: ActiveTermsStore.getState().terms
              };
            },
            isLoadingPrograms () {
              return {
                store: ProgramListStore,
                value: ProgramListStore.getState().loading
              };
            },
            isLoadingTerms () {
              return {
                store: ActiveTermsStore,
                value: ActiveTermsStore.getState().loading
              };
            }
          };

    return <Card>
      <IconCardHeader
        avatar={avatar}
        title="Timeline View"
        subtitle={title}/>
      <CardText>
        <AltContainer stores={timelineStores}>
          <Timeline />
        </AltContainer>
      </CardText>
    </Card>;
  }
});

