import Settings from '../../settings';

export default class DataTableStore {
  constructor () {
    this.loadedEndpoint = null;
    this.data = [];
    this.lastErr = null;
    this.isLoading = false;

    this.bindActions(this.alt.getActions('data-table'));
    this.bindAction(
      this.alt.getActions('table-list').UPDATE_ROUTES,
      this.onUpdateRoutes
    );

    this.exportPublicMethods({
      getData: this.getData,
      getCurrentEndpoint: this.getCurrentEndpoint,
      isLoading: this.getIsLoading
    });
  }

  onFetchTable (data) {
    const { endpoint, params } = data,
          { institution } = this.alt.getStore('institution').getState(),
          query = params ? params : {};

    if (institution) {
      if (query.institution && query.institution !== institution) {
        console.warn('Overriding query parameter `institution` with current store value');
      }
      query.institution = institution;
    }

    this.isLoading = true;
    this.loadedEndpoint = endpoint;
    const promise = (resolve) => {
      request
        .get(Settings.apiRoot + endpoint + '/')
        .query(query)
        .end((err, res) => {
          if (err) {
            this.alt.getActions('data-table').failedFetch(err);
            return resolve();
          }

          let rowData = [];

          for (let rowNum = 0; rowNum < res.body.results.length; rowNum++) {
            const row = res.body.results[rowNum];
            let dataRow = {};
            let col = null;

            for (col in row) {
              if (row.hasOwnProperty(col)) {
                let val = row[col];

                if (val && val._id) {
                  val = val._id;
                }
                dataRow[col] = {
                  content: val
                };
              }
            }
            rowData.push(dataRow);
          }
          this.alt.getActions('data-table').updateTable({ endpoint, rowData });
          return resolve();
        }
      );
    };

    this.alt.resolve(promise);
  }

  onUpdateRoutes () {
    const TableListStore = this.alt.getStore('table-list');

    this.waitFor(TableListStore);

    const routes = TableListStore.getRoutes();

    if (routes.length) {
      this.onFetchTable({ endpoint: routes[0].endpoint });
    }
  }

  onUpdateTable (data) {
    const { endpoint, rowData } = data;

    this.isLoading = false;
    this.loadedEndpoint = endpoint;
    this.data = rowData;
  }

  onFetchFailed (err) {
    this.isLoading = false;
    this.lastErr = err;
  }

  getCurrentEndpoint () {
    return this.getState().loadedEndpoint;
  }

  getData () {
    return this.getState().data;
  }

  getIsLoading () {
    return this.getState().isLoading;
  }
}

