/* @flow */
'use strict';

import request from 'superagent';
import Settings from '../../settings';

export default class ActiveTermsStore {
  constructor () {
    this.terms = [];
    this.loading = false;
    this.lastErr = null;
    this.lastErrStats = {};

    this.bindActions(this.alt.getActions('active-terms'));
    this.bindAction(
      this.alt.getActions('program-list').UPDATE,
      this.onUpdatePrograms
    );
  }

  onFetch (params) {
    if (!this.loading) {
      this.loading = true;
      const promise = (resolve) => {
        const { programs } = this.alt.getStore('program-list').getState(),
              { institution } = this.alt.getStore('institution').getState(),
              starts = [],
              ends = [],
              query = params ? params : {};
        let toPresent = false;

        if (typeof programs !== 'undefined') {
          for (let program of programs) {
            if (program.start_date) {
              starts.push(new Date(program.start_date));
            }
            if (program.end_date) {
              ends.push(new Date(program.end_date));
            } else {
              toPresent = true;
            }
          }
        }

        const activeStart = Math.min(...starts),
              activeEnd = Math.max(...ends);

        /* eslint camelcase: 0 */
        query.institution = institution;
        if (activeStart < Infinity) {
          query.civ_term_start_dt__gte = new Date(activeStart).toISOString().slice(0, 10);
        }
        if (activeEnd > -Infinity && !toPresent) {
          query.civ_term_end_dt__lte = new Date(activeEnd).toISOString().slice(0, 10);
        } else {
          query.civ_term_end_dt__lte = new Date().toISOString().slice(0, 10);
        }

        request
          .get(Settings.apiRoot + 'terms/')
          .query(query)
          .end((err, res) => {
            if (err) {
              this.alt.getActions('active-terms').fetchFailed(err);
              return resolve();
            }

            const terms = res.body.results;

            (async () => {
              await this._fetchStats({ query, terms });
              this.alt.getActions('active-terms').update(
                terms.filter((term) => {
                  return !!term.numStudents;
                })
              );
              return resolve();
            })();
          });
      };

      this.alt.resolve(promise);
    }
  }

  onUpdate (terms) {
    this.loading = false;
    this.terms = terms;
  }

  onFetchFailed (err) {
    this.loading = false;
    this.lastErr = err;
  }

  onUpdatePrograms () {
    this.waitFor(this.alt.getStore('program-list'));
    this.onFetch();
  }

  onFetchStatsFailed (data) {
    const { stat, err } = data;

    this.lastErrStats[stat] = err;
  }

  _fetchStats (data) {
    const { query, terms } = data,
          ids = [];

    for (let term of terms) {
      ids.push(term._id);
    }

    return new Promise((resolve) => {
      const newQuery = {
        institution: query.institution,
        term__civ_term_start_dt__gte: query.civ_term_start_dt__gte,
        term__civ_term_end_dt__lte: query.civ_term_end_dt__lte,
        fields: '*',
        group_by: 'career_term__term'
      };

      request
        .get(Settings.apiRoot + 'student_career_terms/stats/')
        .query(newQuery)
        .end((err, res) => {
          if (err) {
            this.alt.getActions('active-terms').fetchStatsFailed({
              stat: 'numStudents',
              err
            });
          }

          for (let term of terms) {
            if (res.body[term._id]) {
              term.numStudents = res.body[term._id]['*'].count;
            }
          }

          return resolve(terms);
        });
    }).then(() => {
      return new Promise((resolve) => {
        const { programs } = this.alt.getStore('program-list').getState();

        for (let term of terms) {
          term.participationRate = {};
        }
        (async () => {
          await Promise.all(programs.map((program) => {
            return new Promise((resolveInner) => {
              const newQuery = {
                institution: query.institution,
                metric: program.treatment_flag._id,
                term__civ_term_start_dt__gte: query.civ_term_start_dt__gte,
                term__civ_term_end_dt__lte: query.civ_term_end_dt__lte,
                fields: 'actual',
                group_by: 'student_career_term__career_term__term'
              };

              request
                .get(Settings.apiRoot + 'outcomes/stats/')
                .query(newQuery)
                .end((err, res) => {
                  if (err) {
                    this.alt.getActions('active-terms').fetchStatsFailed({
                      stat: 'participationRate',
                      err
                    });
                  }

                  for (let term of terms) {
                    if (res.body[term._id]) {
                      term.participationRate[program._id] = res.body[term._id].actual.mean;
                    }
                  }

                  return resolveInner(terms);
                });
            });
          }));
          return resolve(terms);
        })();
      });
    });
  }
}

