'use strict';
import React from 'react/addons';
import { Mixins } from 'material-ui';
import AltContainer from 'alt/AltContainer';

import { Card, CardText } from './Card';
import { Analysis } from './Analysis';
import IconCardHeader from './IconCardHeader';
import MaterialIcon from './MaterialIcon';

export default React.createClass({
  mixins: [ Mixins.StylePropable ],

  contextTypes: {
    flux: React.PropTypes.object,
    router: React.PropTypes.func
  },

  render () {
    const avatar = <MaterialIcon name='poll'/>,
          ExperimentsStore = this.context.flux.getStore('experiments');

    return <Card>
      <IconCardHeader
        avatar={avatar}
        title="Analysis View"/>
      <CardText>
        <AltContainer store={ExperimentsStore}>
          <Analysis />
        </AltContainer>
      </CardText>
    </Card>;
  }
});

