sourcesContent:
'use strict';
import React from 'react/addons';
import { Mixins } from 'material-ui';

export default React.createClass({
  mixins: [ Mixins.StylePropable ],

  propTypes: {
    ExperimentsStore: React.PropTypes.object,
    ActiveDrilldownsStore: React.PropTypes.object
  },

  componentDidMount () {
    this.renderChart(this.props.ActiveDrilldownsStore.drilldowns);
  },

  componentWillReceiveProps (nextProps) {
    this.renderChart(nextProps.ActiveDrilldownsStore.drilldowns);
  },

  render () {
    return <div className='chart' ref='chartNode'/>;
  },

  renderChart (drilldowns) {
    const node = this.refs.chartNode.getDOMNode(),
          chartConfig = this._chartOptions(drilldowns);

    console.log(chartConfig);

    $(node).highcharts(chartConfig);
  },

  _chartOptions (drilldowns) {
    const series = [
            { name: 'Pilot', data: []},
            { name: 'Control', data: []},
            { name: 'Diff', data: []}
          ],
          categories = [];

    let experiments;

    for (let experimentSet of this.props.ExperimentsStore.experiments) {
      let use = true;
      const other = drilldowns;

      for (let drilldown of experimentSet.drilldowns) {
        if (other.indexOf(drilldown) === -1) {
          use = false;
        }
      }
      for (let drilldown of other) {
        if (experimentSet.drilldowns.indexOf(drilldown) === -1) {
          use = false;
        }
      }

      console.log([ experimentSet.drilldowns, other, use ]);

      if (use) {
        experiments = experimentSet.data;
      }
    }

    if (experiments && experiments.length) {
      for (let experiment of experiments) {
        categories.push(this._chartTitle(experiment.drilldown));
        series[0].data.push(experiment.data.deltaPilot);
        series[1].data.push(experiment.data.deltaControl);
        series[2].data.push(experiment.data.psmDeltaOfDelta);
      }
    }

    return {
      chart: {
        type: 'column'
      },
      title: {
        text: 'Actual Performance vs Predicted'
      },
      xAxis: {
        categories
      },
      series
    };
  },

  _chartTitle (drilldown) {
    const titleParts = [];

    for (let k in drilldown) {
      if (drilldown.hasOwnProperty(k)) {
        titleParts.push(k + '=' + drilldown[k]);
      }
    }

    return titleParts.join(', ');
  }
});

