'use strict';
import React from 'react/addons';
import { BarChart } from 'react-d3-components';
import { Mixins } from 'material-ui';

export default React.createClass({
  mixins: [ Mixins.StylePropable ],

  propTypes: {
    drilldown: React.PropTypes.object,
    data: React.PropTypes.object
  },

  render () {
    const chartConfig = this._chartConfig();

    return <td>
      <BarChart {...chartConfig} />
    </td>;
  },

  _chartConfig () {
    const titleParts = [];

    for (let k in this.props.drilldown) {
      if (this.props.drilldown.hasOwnProperty(k)) {
        titleParts.push(k + '=' + this.props.drilldown[k]);
      }
    }

    const data = [{
            label: titleParts.join(', '),
            values: [
              { x: 'Pilot', y: this.props.data.deltaPilot },
              { x: 'Control', y: this.props.data.deltaControl },
              { x: 'Diff', y: this.props.data.psmDeltaOfDelta }
            ]
          }],
          width = 260,
          height = 210,
          margin = { top: 10, bottom: 50, left: 50, right: 10 };

    return { data, height, width, margin };
  }
});

