    const avatar = <MaterialIcon name='poll'/>,
          { programs } = this.props.ProgramListStore,
          programsMenu = [],
          groupings = [{ type: MenuItem.Types.SUBHEADER, text: 'Drilldowns' }],
          ExperimentsStore = this.context.flux.getStore('experiments'),
          ActiveDrilldownsStore = this.context.flux.getStore('active-drilldowns'),
          availableDrilldowns = [];

    for (let experimentSet of ExperimentsStore.getState().experiments) {
      availableDrilldowns.push(experimentSet.drilldowns);
    }

    if (programs && programs.length) {
      for (let idx = 0; idx < programs.length; idx++) {
        const program = programs[idx];

        programsMenu.push({
          payload: program._id,
          text: program.display_name
        });
      }
    } else {
      programsMenu.push({ text: 'Loading...' });
    }

    if (availableDrilldowns && availableDrilldowns.length) {
      for (let idx = 0; idx < availableDrilldowns.length; idx++) {
        const drilldown = availableDrilldowns[idx];

        groupings.push({
          payload: drilldown,
          text: drilldown.length ? drilldown.join(', ') : 'None'
        });
      }
    } else {
      groupings.push({ text: 'Loading...' });
    }

    return <Card>
      <IconCardHeader
        avatar={avatar}
        title="Analysis View"/>
      <CardMedia>
        <Toolbar>
          <ToolbarGroup key={0} float='left'>
            <ToolbarTitle text='Program'/>
            <DropDownMenu menuItems={programsMenu}/>
          </ToolbarGroup>
          <ToolbarGroup key={1} float='right'>
            <ToolbarTitle text='Options'/>
            <DropDownMenu
              menuItems={groupings}
              onChange={this._onChangeDrilldown}/>
          </ToolbarGroup>
        </Toolbar>
      </CardMedia>
      <CardText>
        <AltContainer stores={{ ExperimentsStore, ActiveDrilldownsStore }}>
          <Analysis />
        </AltContainer>
      </CardText>
    </Card>;
  },

  _onChangeDrilldown (e, key, payload) {
    this.context.flux.getActions('active-drilldowns').update(payload.payload);
  }
});

