'use strict';

export default class ActiveProgramStore {
  constructor () {
    this.program = null;

    this.bindActions(this.alt.getActions('active-program'));
    this.bindActions(
      this.alt.getActions('program-list').UPDATE,
      this.onUpdatePrograms
    );
  }

  onUpdate (program) {
    this.program = program;
  }

  onUpdatePrograms () {
    const { programs } = this.alt.getStore('program-list').getState();

    this.program = null;
    if (programs.length) {
      this.onUpdate(programs[0]);
    }
  }
}

