'use strict';

export default class ActiveDrilldownsStore {
  constructor () {
    this.drilldowns = ['Term'];

    this.bindActions(this.alt.getActions('active-drilldowns'));

    this.bindAction(
      this.alt.getActions('institution').UPDATE,
      this.onUpdateInstitution
    );
  }

  onUpdate (drilldowns) {
    this.drilldowns = drilldowns;
  }

  onClear () {
    this.drilldowns = [];
  }

  onAdd (drilldown) {
    if (!this.drilldowns.every((d) => {
      return d !== drilldown;
    })) {
      this.drilldowns.push(drilldown);
    }
  }

  onRemove (drilldown) {
    const idx = this.drilldowns.indexOf(drilldown);

    if (idx > -1) {
      this.drilldowns.splice(idx, 1);
    }
  }

  onUpdateInstitution () {
    this.onClear();
  }
}
